class Solution {
public:
    double findMedianSortedArrays(vector<int>& nums1, vector<int>& nums2) {
        int m = nums1.size();
        int n = nums2.size();
        //cout << m << " " << n << endl;
        //for(int i = 0; i < m; i++){cout << nums1[i] << " ";} cout << endl;
        //for(int j = 0; j < n; j++){cout << nums2[j] << " ";} cout << endl;

        int left1 = 0;
        int left2 = 0;

        int k = (m + n - 1) >> 1; // 

        while (k > 1) {
            // 
            if (left1 == m) {
                left2 += k;
                k = 0;
                break;
            }
            if (left2 == n) {
                left1 += k;
                k = 0;
                break;
            }

            int mid = k >> 1; //  k > 1

            int mid1 = left1 + mid - 1;
            int mid2 = left2 + mid - 1;

            // k
            if (left1 + mid > m) { //  left1 + mid - 1 >= m
                mid1 = m - 1;
            }
            if (left2 + mid > n) {
                mid2 = n - 1;
            }

            if (nums1[mid1] >= nums2[mid2]) {
                k -= mid2 - left2 + 1; // 
                left2 = mid2 + 1;
            } else {
                k -= mid1 - left1 + 1;
                left1 = mid1 + 1;
            }
        }

        //  k == 1
        if (k == 1) {
            if (left1 == m) {
                left2++;
            } else if (left2 == n) {
                left1++;
            } else {
                if (nums1[left1] >= nums2[left2]) {
                    left2++;
                } else {
                    left1++;
                }
            }
        }

        // 
        if ((m + n) & 1 == 1) { // 
            if (left1 == m) {
                return nums2[left2];
            }
            if (left2 == n) {
                return nums1[left1];
            }
            return std::min(nums1[left1], nums2[left2]);
        } else { // 
            if (left1 == m) {
                return (nums2[left2] + nums2[left2 + 1]) * 1.0 / 2;
            }
            if (left2 == n) {
                return (nums1[left1] + nums1[left1 + 1]) * 1.0 / 2;
            }

            int num1;
            if (nums1[left1] >= nums2[left2]) {
                num1 = nums2[left2];
                left2++;
            } else {
                num1 = nums1[left1];
                left1++;
            }
            int num2;
            if (left1 == m) {
                num2 = nums2[left2];
            }
            else if (left2 == n) {
                num2 = nums1[left1];
            }
            else{
                num2 = min(nums1[left1], nums2[left2]);
            }
            return (num1 + num2) * 1.0 / 2;
        }

        return 0;
    }
};
