        Console.WriteLine(", {0}.", name);
        if(CatShout != null){
            CatShoutEventArgs e = new CatShoutEventArgs();
            e.Name = this.name;
            CatShout(this, e);
        }
    }
}

class Mouse{
    private string name;
    public Mouse(string name){
        this.name = name;
    }

    public void Run(object sender, CatShoutEventArgs args){
        Console.WriteLine("{0}, {1}!", args.Name, name);
    }
}
